static void atest_sha1_core(uint8_t *data, size_t data_size, uint8_t *expected)
{
    Sha1Ctx *ctx = NULL;
    ByteArray *expected_ba = NULL;
    ByteArray *actual_ba = NULL;
    ByteArray *data_ba = NULL;
    size_t count = error_count;

    ctx = sha1_alloc();
    ASSERT_NOT_NULL(ctx);

    expected_ba = ba_alloc_from_uint8(expected, 20);
    data_ba = ba_alloc_from_uint8(data, data_size);

    ASSERT_RET_OK(sha1_update(ctx, data_ba));
    ASSERT_RET_OK(sha1_final(ctx, &actual_ba));

    CHECK_EQUALS_BA(expected_ba, actual_ba);

cleanup:

    PRINT_ERROR(print_hash_error("SHA1", data_ba), count);

    BA_FREE(expected_ba, data_ba, actual_ba);
    sha1_free(ctx);
}

static void atest_sha1_core_update(uint8_t *data, size_t data_size, uint8_t *expected)
{
    Sha1Ctx *ctx = NULL;
    ByteArray *expected_ba = NULL;
    ByteArray *actual_ba = NULL;
    ByteArray *data_ba = NULL;
    ByteArray *print_data_ba = NULL;
    size_t count = error_count;
    size_t len[3];
    size_t i;
    size_t offset = 0;

    len[0] = 1;
    len[1] = 1;
    len[2] = data_size - 2;

    expected_ba = ba_alloc_from_uint8(expected, 20);
    print_data_ba = ba_alloc_from_uint8(data, data_size);

    ctx = sha1_alloc();
    ASSERT_NOT_NULL(ctx);

    for (i = 0; i < (sizeof(len) / sizeof(size_t)); i++) {
        data_ba = ba_alloc_from_uint8(&data[offset], len[i]);
        ASSERT_RET_OK(sha1_update(ctx, data_ba));

        offset += len[i];

        ba_free(data_ba);
        data_ba = NULL;
    }

    ASSERT_RET_OK(sha1_final(ctx, &actual_ba));

    CHECK_EQUALS_BA(expected_ba, actual_ba);

cleanup:

    PRINT_ERROR(print_hash_error("SHA2", print_data_ba), count);

    BA_FREE(expected_ba, data_ba, actual_ba, print_data_ba);
    sha1_free(ctx);
}

static void test_sha1(void)
{
    ByteArray *data_ba = NULL;
    ByteArray *expected_ba = NULL;
    ByteArray *actual_ba = NULL;
    Sha1Ctx *ctx = NULL;
    size_t i = 0;

    atest_sha1_core(&data[0][1], data[0][0], &expected[0][0]);
    atest_sha1_core(&data[1][1], data[1][0], &expected[1][0]);
    atest_sha1_core(&data[2][1], data[2][0], &expected[2][0]);
    atest_sha1_core(&data[3][1], data[3][0], &expected[3][0]);
    atest_sha1_core(&data[4][1], data[4][0], &expected[4][0]);
    atest_sha1_core(&data[5][1], data[5][0], &expected[5][0]);
    atest_sha1_core(&data[6][1], data[6][0], &expected[6][0]);

    atest_sha1_core_update(&data[1][1], data[1][0], &expected[1][0]);
    atest_sha1_core_update(&data[2][1], data[2][0], &expected[2][0]);
    atest_sha1_core_update(&data[3][1], data[3][0], &expected[3][0]);
    atest_sha1_core_update(&data[4][1], data[4][0], &expected[4][0]);
    atest_sha1_core_update(&data[5][1], data[5][0], &expected[5][0]);
    atest_sha1_core_update(&data[6][1], data[6][0], &expected[6][0]);

    /*1000000 repeat test*/
    ctx = sha1_alloc();
    expected_ba = ba_alloc_from_uint8(&expected[7][0], 20);
    data_ba = ba_alloc_from_uint8(&data[7][1], data[7][0]);
    for (i = 0; i < 1000000; i++) {
        sha1_update(ctx, data_ba);
    }
    ASSERT_RET_OK(sha1_final(ctx, &actual_ba));
    equals_ba(expected_ba, actual_ba);

cleanup:

    BA_FREE(expected_ba, data_ba, actual_ba);
    sha1_free(ctx);
}

static void test_sha1_core(HashTestCtx *test_data)
{
    ByteArray *data = ba_alloc_from_le_hex_string(test_data->data);
    ByteArray *expected = ba_alloc_from_le_hex_string(test_data->hash);
    ByteArray *actual = NULL;
    Sha1Ctx *ctx = NULL;

    ctx = sha1_alloc();
    ASSERT_RET_OK(sha1_update(ctx, data));
    ASSERT_RET_OK(sha1_final(ctx, &actual));
    CHECK_EQUALS_BA(expected, actual);

cleanup:

    BA_FREE(data, expected, actual);
    sha1_free(ctx);
}

