static void test_sha1_hmac_core(HmacTestCtx *test_data)
{
    ByteArray *data = ba_alloc_from_str(test_data->data);
    ByteArray *key = ba_alloc_from_le_hex_string(test_data->key);
    ByteArray *expected = ba_alloc_from_le_hex_string(test_data->expected);
    ByteArray *actual = NULL;
    HmacCtx *ctx = NULL;

    ASSERT_NOT_NULL(ctx = hmac_alloc_sha1());
    ASSERT_RET_OK(hmac_init(ctx, key));
    ASSERT_RET_OK(hmac_update(ctx, data));
    ASSERT_RET_OK(hmac_final(ctx, &actual));

    CHECK_EQUALS_BA(expected, actual);

cleanup:

    BA_FREE(data, expected, actual, key);
    hmac_free(ctx);
}

void atest_sha1(void)
{
    size_t err_count = error_count;

    test_sha1();
    ATEST_CORE(SHA1_TEST_DATA, test_sha1_core, sizeof(HashTestCtx));
    ATEST_CORE(sha1_hmac_test_text, test_sha1_hmac_core, sizeof(HmacTestCtx));

    if (err_count == (size_t)error_count) {
        msg_print_atest("SHA1", "[hash, hmac]", "OK");

    } else {
        msg_print_atest("SHA1", "", "FAILED");
    }

    return;
}
